document.addEventListener("DOMContentLoaded",function(){const lightboxLinks=document.querySelectorAll(".lightbox");const overlay=document.getElementById("lightbox-overlay");const lightboxImage=document.getElementById("lightbox-image");const closeBtn=document.querySelector(".lightbox-close");lightboxLinks.forEach(link=>{link.addEventListener("click",function(e){e.preventDefault();const imageSrc=this.getAttribute("href");lightboxImage.setAttribute("src",imageSrc);overlay.style.display="flex";});});closeBtn.addEventListener("click",function(){overlay.style.display="none";lightboxImage.setAttribute("src","");});overlay.addEventListener("click",function(e){if(e.target===overlay){overlay.style.display="none";lightboxImage.setAttribute("src","");}});});