console.log("✅ Lightbox.js chargé !");document.addEventListener("DOMContentLoaded",function(){const images=document.querySelectorAll(".photo-item img");const lightbox=document.getElementById("lightbox-overlay");const lightboxImage=document.getElementById("lightbox-image");const closeBtn=document.querySelector(".lightbox-close");const nextBtn=document.querySelector(".lightbox-next");const prevBtn=document.querySelector(".lightbox-prev");let currentIndex=0;function showImage(index){lightboxImage.src=images[index].src;currentIndex=index;}
images.forEach((img,index)=>{img.addEventListener("click",()=>{lightbox.style.display="flex";showImage(index);});});closeBtn.addEventListener("click",()=>{lightbox.style.display="none";});nextBtn.addEventListener("click",()=>{currentIndex=(currentIndex+1)%images.length;showImage(currentIndex);});prevBtn.addEventListener("click",()=>{currentIndex=(currentIndex-1+images.length)%images.length;showImage(currentIndex);});document.addEventListener("keydown",(e)=>{if(e.key==="Escape"){lightbox.style.display="none";}});});